      return <Sobre/> // se for no endereco '/sobre' vai para a pagina sobre.js
    }else{
      return <Home /> //se for para qualquer outro endereco vai pra Home.js
    }
  }
  return (
  <>
    {Router ()}
  </>
  )
}

export default App
